import { React, useEffect } from 'react';
import { useState } from 'react'
import { slideInLeft } from 'react-animations';
import { slideInRight } from 'react-animations';
import { slideInUp } from 'react-animations';
import Radium, { StyleRoot } from 'radium';
import Shop from './Shop';
import Pic from './cart.svg';
import './Card.css';
import { db } from '../firebase/firebase';
import RazorPanel from './razorpay';
import C_Prod from './C_Prod';
import Invoice from './Invoice';
import { FaProcedures } from 'react-icons/fa';
import { FaTimes } from 'react-icons/fa'

const Cart = ({uid}) => {
  const style1 = {
    slideInLeft: {
      animation: 'x 2s',
      animationName: Radium.keyframes(slideInLeft, 'slideInLeft')
    }
  }
  const style2 = {
    slideInRight: {
      animation: 'x 2s',
      animationName: Radium.keyframes(slideInRight, 'slideInRight')
    }
  }
  const style3 = {
    slideInUp: {
      animation: 'x 1s',
      animationName: Radium.keyframes(slideInUp, 'slideInUp')
    }
  }
  const [status, setStatus] = useState("Home");
  const [items, setItems] = useState([]);
  const [total, setTotal] = useState(0);
  const goBack = (e) => {
    e.preventDefault();
    setStatus("Back");
  }
  const viewItems = (e) => {
    e.preventDefault();
    if(items.length > 0)
      setStatus("view");
    else
      setStatus("noview");
  }
  const proceed = (e) => {
    e.preventDefault();
    var cost = 0;
    items.map((item) => {
      cost += (parseInt(item.price)*parseInt(item.quantity));
    });
    console.log(items);
    console.log(cost);
    setTotal(cost);
    setStatus("Pay");
  }

  const cancel = (e) => {

    items.map(async (item) => {     
      console.log(item.doc_id);
      const res1 = await db.collection('Cart').doc(String(item.doc_id)).delete();
    });
    alert("Your cart has been cleared successfully !");
    setStatus("Back");
  }

  const remove = async(item) => {

    var flag = 0;
    console.log(item.item_code);

    for (var i = 0; i < items.length; i++) 
    {
      if (items[i].item_code === item.item_code) 
      {
        items.splice(i, 1);
        i--;
        flag = 1;
      }
    }

    const res = await db.collection('Cart').doc(String(item.doc_id)).delete();

    if (status === "view" || status === "noview") 
    {
      if(items.length > 0)
        setStatus("viewagain");
      else
        setStatus("noview")
    }
    else 
    {
      if(items.length > 0)
        setStatus("view");
      else
        setStatus("noview")
    }
  }

 useEffect(async() => {
  const citiesRef = db.collection('Cart');
  const snapshot = await citiesRef.get();
  var cost = 0;
  if(status === "Home")
  {
    snapshot.forEach(doc => {
      if(doc.data().customer_id === String(uid))
      {
        var item = {
          doc_id: doc.id,
          item_code: doc.data().item_code,
          product: doc.data().product,
          price : doc.data().price, 
          quantity: doc.data().quantity
        }

        console.log(item);
        items.push(item);
      }
    }); 
  }
  console.log(items);
 })
  const checkStatus = () => {
    if(status === "Home")
    {
      return  <div className="container">
